#!/usr/bin/pythonimport sysimport jsonimport randomclass Get_UA:	def __init__(self, device=None, limit=None):		self.max = 501		self.limit = limit		self.device = device.lower()		if self.device and self.limit:			self.findUserAgents()	def findUserAgents(self):		with open("./get_useragents/data/get_useragents.json", "r") as f:			data = json.loads(f.read())				self.user_agents = data["g4rzk"][f"{self.device}"]	def GetUserAgents(self):		while True:			return self.user_agents			self.count +=1			if self.count >= self.max:				break	def RandomUserAgents(self):		return random.choice(self.user_agents)